        self.stack2 = []

    def enqueue(self, value):
        # Push the element onto the first stack
        self.stack1.append(value)

    def dequeue(self):
        if not self.stack2:
            # If the second stack is empty, transfer the elements from the first stack to the second stack
            while self.stack1:
