#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

int countWordOccurrences(const std::string& line, const std::string& word) {
    std::istringstream iss(line);
    std::string currentWord;
    int count = 0;

    while (iss >> currentWord) {
        if (currentWord == word) {
            count++;
        }
    }

    return count;
